import React, {useState, useRef} from 'react';

function InputCity() {
    const currCity = userRef();

    function currentCity() {
        console.log(currCity.current.value);
    }

    return (
        <div>
            <input type='text' ref={currCity} placeholder='Введите название города' />
            <input type='button' onClick={currentCity} value='+'/>
        </div>
    );
}

export default InputCity;
