import React, {useState, useRef} from 'react';
import './inputCity_style.css'

function InputCity() {
    const currCity = useRef();
     const [selectedCity, setSelectedCity] = useState();

    function currentCity() {
        selectedCity = currCity.current.value;
        return selectedCity;
    }

    return (
        <div>
            <input type='text' ref={currCity} placeholder='Введите название города' />
            <input type='button' onClick={currentCity} value='+'/>
        </div>
    );
}

export default InputCity;
