import React, {useState} from 'react';
import './App.css';
import InputCity from './components/inputCity/inputCity';
import Widget from "./components/widget/widget";

function App(props) {
  const [cities, setCities] = useState([]);
  function onButtonPress(props) {
    setCities(cities => [...cities, props]);
    console.log(cities);
  }

  function deleteCities(props) {
      console.log(props);
      setCities(prevState => {
          cities: prevState.cities.filter(props => props !== props.name)
      });
      }

  return (
      <div className='wrapper'>
          <InputCity onButtonPress={onButtonPress} />
          <div className='widget_wrapper'> {
              cities.map((el) => <Widget name={el} key={el} deleteCities={deleteCities} />)
            }
          </div>
      </div>
  );
}

export default App;
