import React, {useState, useRef} from 'react';
import './inputCity_style.css'

function InputCity() {
    const currCity = useRef();
     const [selectedCity, setSelectedCity] = useState();
     console.log(props);
    function currentCity(vent) {
        setSelectedCity(event.e.value);
    }

    return (
        <div>
            <input type='text' value={selectedCity} onChange={(e) => {currentCity(e)}} placeholder='Введите имя первого игрока'/>
            <input type='button' onClick={currentCity} value='+'/>
        </div>
    );
}

export default InputCity;
