import React, {useRef} from 'react';
import './inputCity_style.css'

function InputCity(props) {
    const currCity = useRef();

    function currentCity() {
        const selectedCity = currCity.current.value;
        return selectedCity;
    }

    return (
        <div>
            <input type='text' ref={currCity} placeholder='Введите название города' />
            <input type='button' onClick={() => {props.onButtonPress(props.currentCity);}}  value='+'/>
        </div>
    );
}

export default InputCity;
