import React, {useRef} from 'react';
import './inputCity_style.css'

function InputCity(props) {
    const currCity = useRef();

    onKeyDown = (event: React.KeyboardEvent<HTMLDivElement>): void => {
        // 'keypress' event misbehaves on mobile so we track 'Enter' key via 'keydown' event
        if (event.key === 'Enter') {
            event.preventDefault();
            event.stopPropagation();
            this.onSubmit();
        }
    }

    return (
        <div className='input'>
            <div className='input__container'>
                <input onKeyDown={onKeyDown} type='text' ref={currCity} required />
                <label>Введите название города</label>
            </div>
            <input  type='button'  onClick={() => {props.onButtonPress(currCity.current.value)}}  value='+'/>
        </div>
    );
}

export default InputCity;
