import React, {useState} from 'react';
import './App.css';
import InputCity from './components/inputCity/inputCity';
import Widget from "./components/widget/widget";
import axios from "axios";


function App(props) {
  const [cities, setCities] = useState([]);

  function onButtonPress(props) {
      axios.get('http://localhost:4000/info').then((response) => {
          handleGame(response.data)
      });
    setCities(cities => [...cities, props]);
    console.log(cities);
  }

  function deleteCities(props) {
      console.log(props);
      setCities(cities.filter((key) => key !== props.name));
  }

  return (
      <div className='wrapper'>
          <InputCity onButtonPress={onButtonPress} />
          <div className='widgets_wrapper'> {
              cities.map((el) => <Widget name={el} key={el} deleteCities={deleteCities} />)
            }
          </div>
      </div>
  );
}

export default App;
